export default function setCookie(cname, cvalue, exdays) {
  const hours = 1;
  const d = new Date();
  d.setTime(d.getTime() + (exdays*hours*60*60*1000));
  let expires = "expires=" + d.toUTCString();
  document.cookie = cname + '=' + cvalue + ';' + expires + ';path=/';
}
